    });
  };


    return (
        <div>
            <h1>Login Page</h1>
            <a href="http://localhost:3000/">Home</a>
            <div>
                <input
                    type="text"
                    placeholder="username..."
                    value={username}
                    onChange={(event) => {
                        setUsername(event.target.value);
                        }}
                />
                <input 
                    type="password" 
                    placeholder="password..."
                    value={password}
                    onChange={(event) => {
                        setPassword(event.target.value);
                        }}
                />
                <button onClick={loginUser}>
                    Login
                </button>
            </div>
        </div>
    );
}

export default Login;
