import React from "react";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Matches from "./ChatComponents/matches.jsx";
import MessageBubble from "./ChatComponents/messageBubbles.jsx";

function Chats(){
    const username = localStorage.getItem("userID");
    const navigate = useNavigate();

    //change to contain data from database like editpref???
    const [message, setMessage] = useState("");

    const sendMessage = async (event) => {
        event.preventDefault();
        try{
            await axios.post("http://localhost:3001/chats", {message : message, username : username, contact : "lucas"});
            alert("Message sent");
        } catch (err){
            console.error(err);
        }
    };

    const getMessage = async (event) => {
        event.preventDefault();
        try {
            await axios.get("http://localhost:3001/chats", {
                params: { username : username, contact : "lucas" }
            });
            alert("New Message");
        } catch (err) {
            console.error(err);
        }
    }

    const NoChatSelected = () => {
        return (
            <div className="message-box">
                <p>Start Chatting!</p>            
            </div>
        )
    }

    const isChat = true;
    return (

        <div>
            <div className="chat">
                <div className="match-box">
                    <Matches />
                    <Matches />
                    <Matches />
                    <Matches />
                    <Matches />
                </div>

                <div className="chat-divider"></div>

                {isChat ? (
                    <NoChatSelected />
                ) : (
                    <div className="message-box">
                        <MessageBubble />
                        <MessageBubble />
                        <MessageBubble />
                        <MessageBubble />
                        <MessageBubble />
                        <MessageBubble />
                    </div>
                )}
                
            </div>
            
            <label>
                <input
                id="first_name"
                type="text"
                required={true}
                placeholder="Write your messaage here..."
                value={message}
                onChange={(event) => {
                    setMessage(event.target.value);
                }}
                />
            </label>
            <button className = "btn btn-primary" onClick = {sendMessage}>
                Send Message
            </button>

            <button className = "btn btn-primary" onClick = {getMessage}>
                Get Message
            </button>
        </div>
    )
}

export default Chats
