import React from "react";
import { useState , useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Matches from "./ChatComponents/matches.jsx";
import MessageBubble from "./ChatComponents/messageBubbles.jsx";
import useMatch from "../zustand/useMatch.js";

function Chats(){
    const username = localStorage.getItem("userID");
    const navigate = useNavigate();
    const { selectedMatch, setSelectedMatch } = useMatch();

    //change to contain data from database like editpref???
    const [message, setMessage] = useState("");

    const sendMessage = async (event) => {
        event.preventDefault();
        try{
            await axios.post("http://localhost:3001/chats", {message : message, username : username, contact : "lucas"});
            alert("Message sent");
        } catch (err){
            console.error(err);
        }
    };


    const [textMessages, setTextMessages] = useState([]);

    const getMessages = async (event) => {
        event.preventDefault();
        console.log("Selected Match: ", selectedMatch._id);
        try {
            const res = await axios.get("http://localhost:3001/chats/:id", {
                params: { username : username, contact : "lucas" }
            });
            setTextMessages(res.data);
            console.log("Messages: ", res.data);
            alert("New Message");
        } catch (err) {
            console.error(err);
        }
        return { textMessages };
    }
    
    

    const useGetMatches = () => {
        const [matches, setMatches] = useState([]);

        useEffect(() => {
            const getMatches = async () => {
                try {
                    
                    const res = await axios.get("http://localhost:3001/chats", {
                        params: { username : username }
                    });
                    setMatches(res.data);
                    console.log("Lookie Here: ", res.data)
                    alert("Matches Found!");
                } catch (err) {
                    console.error(err);
                }
            }
            getMatches();
        }, []);

        return { matches };
    }

    const NoChatSelected = () => {
        return (
            <div className="message-box">
                <p>Start Chatting!</p>            
            </div>
        )
    }

    const { matches } = useGetMatches();
    //const matches = matchObjects['matches'];

    return (
        
        <div>
            
            <div className="chat">
                <div className="match-box">
                    {matches.map((match) => (
                        <Matches 
                            key={match._id}
                            match={match}
                        />
                    ))}
                </div>

                <div className="chat-divider"></div>

                {!selectedMatch ? (
                    <NoChatSelected />
                ) : (
                    <div className="message-box">
                        {textMessages.map((textMessage) => (
                            <MessageBubble 
                                key={textMessage._id}
                                textMessage={textMessage}
                            />
                        ))}

                        <label>
                            <input
                                id="first_name"
                                type="text"
                                required={true}
                                placeholder="Write your messaage here..."
                                value={message}
                                onChange={(event) => {
                                    setMessage(event.target.value);
                                }}
                                />
                        </label>
                        <button className = "btn btn-primary" onClick = {sendMessage}>
                            Send Message
                        </button>
                    </div>
                )}
                
            </div>
            
            

            <button className = "btn btn-primary" onClick = {getMessages}>
                Get Message
            </button>
        </div>
    )
}

export default Chats
