import React from "react";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";


function EditPreferences() {

    const [football, setfootball] = useState(false);
    const [basketball, setbasketball] = useState(false);
    const [tennis, settennis] = useState(false);
    //const [sex, setsex] = 
    const username = localStorage.getItem("userID");
    const naviagate = useNavigate();


    const editPref = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post("http://localhost:3001/editPreferences", 
            {   username: username, 
                //sex: sex,
                football: football, 
                basketball: basketball, 
                tennis: tennis,
            });
            naviagate("/");
        }
        catch(err) {
            console.error(err);
        }
    };

    document.addEventListener("DOMContentLoaded", function() {
        const checkboxes = document.querySelectorAll('input[type="checkbox"][name="checkbox-option"]');
      
        checkboxes.forEach(function(checkbox) {
          checkbox.addEventListener("change", function() {
            checkboxes.forEach(function(otherCheckbox) {
              if (otherCheckbox !== checkbox) {
                otherCheckbox.checked = false;
              }
            });
          });
        });
    });


    return (
        <div>
            <a href="/" class="home">Home</a>

            <div class="checkboxes">
            <label>
                <input type="checkbox" name="checkbox-option" value="option1"></input>
                Male
            </label>
            <label>
                <input type="checkbox" name="checkbox-option" value="option2"></input>
                Female
            </label>
            </div>

            <div>
                <label>Football</label>
                <input 
                type="checkbox" 
                onClick = {(event) => {
                    if (football) {
                        setfootball(false);
                    } else {
                        setfootball(true);
                    }
                }}>
                </input>
            </div>
            <div>
                <label>Basketball</label>
                <input 
                type="checkbox" 
                onClick = {(event) => {
                    if (basketball) {
                        setbasketball(false);
                    } else {
                        setbasketball(true);
                    }
                }}>
                </input>
            </div>
            <div>
                <label>Tennis</label>
                <input 
                type="checkbox" 
                onClick = {(event) => {
                    if (tennis) {
                        settennis(false);
                    } else {
                        settennis(true);
                    }
                }}>
                </input>
            </div>
            <div>
                <button onClick= {editPref}>Submit</button>
            </div>
        </div>
    );
}


export default EditPreferences
